<?phpabstract class C_controller {    function __construct (){    }    public function request (){        $this->OnInput();        $this->OnOutput();    }    protected function OnInput (){    }    protected function OnOutput (){    }    protected function IsGET (){        return $_SERVER ['REQUEST_METHOD']=='GET';    }    protected function IsPOST (){        return $_SERVER ['REQUEST_METHOD']=='POST';    }    public function view_include($fileName, $vars = array())    {        foreach ($vars as $name => $value) {            $$name = $value;        }        ob_start();        include $fileName;        return ob_get_clean();    }}